import React from "react";
import { Container, Row, Col, Form } from "react-bootstrap";
function BookTicket(props) {
  let seatLayout = [];
  for (let c = 0; c < props.selectedEventDetails.total_seat_count; c++) {
    seatLayout.push(<div className="grid-item">c</div>);
  }
  //let style = `--grid-rows:${props.selectedEventDetails.hall.total_rows}, --grid-cols:${props.selectedEventDetails.hall.total_columns}`;
  return (
    <Container>
      <Form>
        <Form.Group controlId="noOfSeats">
          <Form.Label>How many Seats?</Form.Label>
          <Form.Control as="select">
            <option>1</option>
            <option>2</option>
            <option>3</option>
            <option>4</option>
            <option>5</option>
            <option>6</option>
            <option>7</option>
            <option>8</option>
            <option>9</option>
            <option>10</option>
          </Form.Control>
        </Form.Group>
      </Form>
      <Row>
        <Col md={6} className="text-mute">
          <span className="text-mute"> Show Date:</span>
          <span className="text-danger">
            {props.selectedEventDetails.show_date}
          </span>
        </Col>
        <Col md={6} className="text-mute">
          <span className="text-mute"> Show Time:</span>
          <span className="text-danger">
            {props.selectedEventDetails.show_time}
          </span>
        </Col>
      </Row>
      <div className="seat-layout-wrap">{seatLayout}</div>
    </Container>
  );
}

export default BookTicket;
