import React, { useState } from "react";
import { Navbar, Container, Row, Col, Form } from "react-bootstrap";
import config from "../config.json";
import "./header.scss";
import logo from "../logo.svg";

const Header = props => {
  const [searchTxt, setSearchTxt] = useState("");
  function searchMovies(e) {
    e.preventDefault();
    setSearchTxt(e.target.value);
    fetch(`${config.apiUrl}/api/movies/query/${searchTxt}`).then(response => {
      console.log(response);
      //  response.json()
    });
  }
  return (
    <header>
      <Container className="header">
        <Row className="justify-content-space-between">
          <Col xs={3} md={2} lg={1} className="p-0">
            <Navbar className="px-sm-0">
              <Navbar.Brand href="/" className="p-0">
                <img
                  alt=""
                  src={logo}
                  height="60"
                  className="logo d-inline-block align-top"
                />
              </Navbar.Brand>
            </Navbar>
          </Col>
          <Col xs={3} className="p-0">
            <h1>{"Book my Movie"}</h1>
          </Col>
          <Form className="col" onSubmit={this.searchMovies}>
            <Form.Control
              type="text"
              name="search"
              placeholder="search for movies"
            />
          </Form>
        </Row>
      </Container>
    </header>
  );
};

export default Header;
