import React, { Component } from "react";
import config from "../config.json";
import ShowsListComponent from "./ShowsList.component";

class ShowsList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      eventsList: [],
      selectedEventUpdate: props.selectedEventUpdater
    };
    console.log("Show List Container:", props);
  }
  componentDidMount() {
    let urlArray = window.location.pathname.split("/");

    const movieId = urlArray[urlArray.length - 1];
    //console.log("movie id", movieId);

    fetch(`${config.apiUrl}/api/events/shows/movie/${movieId}`)
      .then(response => response.json())
      .then(result => {
        //console.log("result:", result);
        this.setState({ eventsList: result });
      });

    console.log(this.state.selectedEventUpdate);
  }
  render() {
    return (
      <ShowsListComponent
        eventsList={this.state.eventsList}
        selectedEventUpdater={this.state.selectedEventUpdate}
      />
    );
  }
}

ShowsList.defaultProps = {
  eventsList: []
};
export default ShowsList;
