import React from "react";
import { Container, Row, Col } from "react-bootstrap";
import { FaHeart } from "react-icons/fa";

function MovieDetails(props) {
  return (
    <Container className="movie-details-wrap">
      {console.log(props)}
      <Row>
        <Col md={4}>
          <img src={props.imgsrc} />
        </Col>
        <Col md={8}>
          <h2 className="text-dark">{props.name}</h2>
          <br />
          <h5 className="text-mute">Release Date: {props.releaseDate}</h5>
          <h5 className="text-mute">Language: {props.lang}</h5>
          <hr />
          <button type="button" class="btn btn-success btn-sm">
            {props.category}
          </button>
          {/* {props.category.map(c => {
            return (
              <button type="button" class="btn btn-success btn-sm">
                c
              </button>
            );
          })} */}
          <br />
          <hr />
          <div className="text-danger">
            <FaHeart /> {props.avg_rating} | {props.voting_count}
          </div>
        </Col>
      </Row>
    </Container>
  );
}

export default MovieDetails;
