import React from "react";
import { Container, Row, Col } from "react-bootstrap";
function MovieDetails(props) {
  return (
    <Container className="movie-details-wrap">
      <Row>
        <Col md={4}>
          <img src={props.imgsrc} />
        </Col>
        <Col md={8}>
          <h2 className="text-dark">{props.name}</h2>
          <br />
          <h5 className="text-mute">Release Date: {props.releaseDate}</h5>
          <h5 className="text-mute">Language: {props.lang}</h5>

          {/*{props.category.split(" ")} {props.category.split().map(c => {
            return (
              <button type="button" class="btn btn-success btn-sm">
                c
              </button>
            );
          })} */}
        </Col>
      </Row>
    </Container>
  );
}

export default MovieDetails;
