import React, { Component } from "react";
import config from "../config.json";
import BookTicketComponent from "./BookTicket.component";

class BookTicket extends Component {
  constructor(props) {
    super(props);
    this.state = {
      eventsList: []
    };
    console.log(props.currentMovieDetails);
  }
  componentDidMount() {
    let urlArray = window.location.pathname.split("/");

    const movieId = urlArray[urlArray.length - 1];
    console.log("movie id", movieId);

    fetch(`${config.apiUrl}/api/events/shows/movie/${movieId}`)
      .then(response => response.json())
      .then(result => {
        //console.log("result:", result);
        this.setState({ eventsList: result });
      });
  }
  render() {
    // console.log("the props from parent");
    // let { moviedetails } = this.props;
    // console.log(moviedetails);

    const movieDetails = {
      poster_path: "",
      name: "XYZ",
      tagline: "qweqweqdfcereqw",
      releaseDate: "28-11-2019",
      runtime: "121"
    };
    return (
      <BookTicketComponent
        movieDetails={movieDetails}
        eventsList={this.state.eventsList}
      />
    );
  }
}

export default BookTicket;
