import React, { useState } from 'react';
import { Router, Route, Switch } from 'react-router-dom';
import { Container } from 'react-bootstrap';

import './App.scss';

import Header from './common/Header.component';
import Footer from './common/Footer.component';

import ProductListing from './MovieListing/ProductListing.container';
import ProductDetails from './MovieDetails/ProductDetails.container';
import Cart from './Cart';
import history from './history';

function App() {
	const [cartCount, updateCartCount] = useState(0);

	return (
		<Router history={history}>
			<div className="App">
				<Header cartCount={cartCount} />
				<main>
					<Container>
						<Switch>
							<Route
								path="/"
								exact
								render={() => (
									<ProductListing cartCountUpdater={() => updateCartCount(cartCount + 1)} />
								)}
							/>
							<Route path="/product/:id" component={ProductDetails} />
							<Route path="/cart" component={Cart} />
						</Switch>
					</Container>
				</main>
				<Footer />
			</div>
		</Router>
	);
}

export default App;
