import React, { Component } from "react";
import { Router, Route, Switch } from "react-router-dom";
import { Container, ThemeProvider } from "react-bootstrap";

import "./App.scss";

import Header from "./common/Header.component";
import Footer from "./common/Footer.component";

import MovieListing from "./MovieListing/MovieListing.container";
import MovieDetails from "./MovieDetails/MovieDetails.container";
import BookTicket from "./BookTicket/BookTicket.container";

import Cart from "./Cart";
import history from "./history";

class App extends Component {
  constructor() {
    this.state = {
      selectedMovieDetail: {}
    };
    this.updateSectedMovieDetail = this.updateSectedMovieDetail.bind(this);
  }
  updateSectedMovieDetail(selectedMovie) {
    this.setState({ selectedMovieDetail: selectedMovie });
  }
  render() {
    return (
      <Router history={history}>
        <div className="App">
          <Header />
          <main>
            <Container>
              <Switch>
                <Route
                  path="/"
                  exact
                  render={() => (
                    <MovieListing onClick={this.updateSectedMovieDetail} />
                  )}
                />
                <Route path="/movie/:id" render={() => <MovieDetails />} />
                <Route path="/booking/:id" render={() => <BookTicket />} />
                <Route path="/checkout" component={Cart} />
              </Switch>
            </Container>
          </main>
          <Footer />
        </div>
      </Router>
    );
  }
}

export default App;
